import {AfterViewInit, Component, OnInit} from '@angular/core';
import {ClientService, Order} from "../client.service";
import {interval} from "rxjs";

@Component({
  selector: 'app-order-status',
  templateUrl: './order-status.component.html',
  styleUrls: ['./order-status.component.scss']
})
export class OrderStatusComponent implements OnInit, AfterViewInit {
  order: Order;

  constructor(
    private clientService: ClientService) { }

  ngOnInit(): void {
    this.assignOrder();
  }

  ngAfterViewInit(): void {
    this.assignOrder();
    this.updateOrder().then();
  }

  async updateOrder() {
    console.log('updateOrder accessed')
    let count = 1;
    let milliseconds = 20000;
    let sub = interval(milliseconds).subscribe(() => {
      console.log(milliseconds*count/1000+' seconds passed: Running \'updateOrder\' again with count='+count)
      count++;
      this.assignOrder();
      if (this.order.status=='finished') {
        sub.unsubscribe();
      }
    })
  }

  assignOrder () {
    this.clientService.getOrder()
      .subscribe((orderFetched: Order)=>{
        this.order = orderFetched;
      });
}

  onSubmit() { //todo: implement this in html
    this.clientService.changeClientTaskStatusAndRedirect('task_2');
  }
}
<app-client></app-client>


<div class="container-fluid">
  <!-- row 4x4x4-->
  <div class="row">
    <div class="col-md-4">
    </div>
    <div class="col-md-4">
      <p start>Your Order is accepted! Please follow the Status below:</p>
      <table class="table">
        <thead>
        <tr>
          <th scope="col">Order Id</th>
          <th scope="col">Client Name</th>
          <th scope="col">Delivery Address</th>
          <th scope="col">Order Time</th>
          <th scope="col">Paid online?</th>
          <th scope="col">Order Status</th>
        </tr>
        </thead>
        <tbody>
        <tr>
          <th scope="row">{{order.id | slice:0:8}}</th>
          <td>{{order.clientName}}</td>
          <td>{{order.address}}</td>
          <td>{{order.orderTime}}</td>
          <td>{{order.paid}}</td>
          <td>{{order.status | uppercase}}</td>
        </tbody>
      </table>
    </div>
    <div class="col-md-4">
    </div>
    <p></p>

    <div class="row">
      <div class="col-md-3">
      </div>
      <div class="col-md-6">
        <table class="bi-table">
          <tbody>
          <tr>
            <th><p>Confirmed</p><img *ngIf="order.status=='confirmed'" alt="Responsive image" class="img-fluid" src="assets/images/delivery-guy-in-bike.png"></th>
            <th><p>Accepted</p><img *ngIf="order.status=='accepted'" alt="Responsive image" class="img-fluid" src="assets/images/delivery-guy-in-bike.png"></th>
            <th><p>Baking</p><img *ngIf="order.status=='baking'" alt="Responsive image" class="img-fluid" src="assets/images/delivery-guy-in-bike.png"></th>
            <th><p>Pizza Ready</p><img *ngIf="order.status=='pizzaReady'" alt="Responsive image" class="img-fluid" src="assets/images/delivery-guy-in-bike.png"></th>
            <th><p>Delivering</p><img *ngIf="order.status=='delivering'" alt="Responsive image" class="img-fluid" src="assets/images/delivery-guy-in-bike.png"></th>
            <th><p>Pizza Delivered</p><img *ngIf="order.status=='pizzaDelivered'" alt="Responsive image" class="img-fluid" src="assets/images/delivery-guy-in-bike.png"></th>
            <th><p>Finished </p><img *ngIf="order.status=='finished'" alt="Responsive image" class="img-fluid" src="assets/images/delivery-guy-in-bike.png"></th>
          </tr>
          </tbody>
        </table>
        <img alt="Responsive image" class="rounded mx-auto d-block" src="assets/images/track.png">
        <div class="col-md-3">
        </div>
    </div>

  </div>
    <!--          row 4x4x4-->
    <div class="row">
      <p></p>
      <div class="col-md-4"></div>
      <div class="col-md-2">
        <button [disabled]="order.status!='finished'" class="w-100 btn btn-lg btn-info" type="submit">End My Status Check</button>
      </div>
      <div class="col-md-2">
        <button class="w-100 btn btn-lg btn-secondary" [routerLink]="['/client']" skipLocationChange="true" >Back</button>
      </div>
      <div class="col-md-4"></div>
    </div>
    <p></p>
</div>
</div>
