import {AfterViewInit, Component, OnInit} from '@angular/core';
import {ClientService, Order} from "../client.service";
import {Observable, interval } from "rxjs";

@Component({
  selector: 'app-order-status',
  templateUrl: './order-status.component.html',
  styleUrls: ['./order-status.component.scss']
})
export class OrderStatusComponent implements OnInit, AfterViewInit {
  order = ClientService.order;

  constructor(
    private clientService: ClientService) { }

  ngOnInit(): void {
  }

  ngAfterViewInit(): void {
    this.order = ClientService.order;
    this.updateOrder().then();
  }
  async updateOrder() {
    console.log('updateOrder accessed')
    let sub = interval(10000).subscribe(() => {
      this.clientService.getOrder();
      this.order = ClientService.order;
      if (this.order.status=='finished') {
        sub.unsubscribe();
      }
    })

  }
}
<app-client></app-client>


<div class="container-fluid">
  <!-- row 4x4x4-->
  <div class="row">
    <div class="col-md-4">
    </div>
    <div class="col-md-4">
      <p start>Your Order is accepted! Please follow the Status below:</p>
      <table class="table">
        <thead>
        <tr>
          <th scope="col">Order Id</th>
          <th scope="col">Client Name</th>
          <th scope="col">Delivery Address</th>
          <th scope="col">Order Time</th>
          <th scope="col">Paid online?</th>
          <th scope="col">Order Status</th>
        </tr>
        </thead>
        <tbody>
        <tr>
          <th scope="row">{{order.id | slice:0:8}}</th>
          <td>{{order.clientName}}</td>
          <td>{{order.address}}</td>
          <td>{{order.orderTime}}</td>
          <td>{{order.paid}}</td>
          <td>{{order.status | uppercase}}</td>
        </tbody>
      </table>
    </div>
    <div class="col-md-4">
    </div>

    <div class="row">
      <div class="col-md-3">
      </div>
      <div class="col-md-7">
      <div class="col-md-1"><p>Confirmed - </p><img src="assets/images/delivery-guy-in-bike.png" class="img-fluid" alt="Responsive image" *ngIf="order.status=='confirmed'"></div>
        <div class="col-md-1"><p>Accepted - </p><img src="assets/images/delivery-guy-in-bike.png" class="img-fluid" alt="Responsive image" *ngIf="order.status=='accepted'"></div>
        <div class="col-md-1"><p>Baking - </p><img src="assets/images/delivery-guy-in-bike.png" class="img-fluid" alt="Responsive image" *ngIf="order.status=='baking'"></div>
        <div class="col-md-1"><p>Pizza Ready - </p><img src="assets/images/delivery-guy-in-bike.png" class="img-fluid" alt="Responsive image" *ngIf="order.status=='pizzaReady'"></div>
        <div class="col-md-1"><p>Delivering - </p><img src="assets/images/delivery-guy-in-bike.png" class="img-fluid" alt="Responsive image" *ngIf="order.status=='delivering'"></div>
        <div class="col-md-1"><p>Pizza Delivered - </p><img src="assets/images/delivery-guy-in-bike.png" class="img-fluid" alt="Responsive image" *ngIf="order.status=='pizzaDelivered'"></div>
        <div class="col-md-1"><p>Finished </p><img src="assets/images/delivery-guy-in-bike.png" class="img-fluid" alt="Responsive image" *ngIf="order.status=='finished'"></div>
        <img src="assets/images/track.png" class="img-fluid" alt="Responsive image">
        <div class="col-md-2">
      </div>
    </div>

  </div>
</div>
